      this.status,
    this.message,
    this.code,
      this.totalResults, 
      this.articles,});

  NewsResponse.fromJson(dynamic json) {
    status = json['status'];
    message = json['message'];
    code = json['code'];

    totalResults = json['totalResults'];
    if (json['articles'] != null) {
      articles = [];
      json['articles'].forEach((v) {
        articles?.add(News.fromJson(v));
      });
    }
  }
  String? status;
  num? totalResults;
  String? message ;
  String? code ;
  List<News>? articles;
